document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll("[data-include]").forEach((function(e){const t=e.getAttribute("data-include");fetch(t).then((e=>{if(!e.ok)throw new Error(`HTTP error! Status: ${e.status}`);return e.text()})).then((n=>{e.innerHTML=n;e.querySelectorAll("script").forEach((e=>{const t=document.createElement("script");Array.from(e.attributes).forEach((e=>{t.setAttribute(e.name,e.value)})),t.innerHTML=e.innerHTML,e.parentNode.replaceChild(t,e)}));const r=new CustomEvent("includeLoaded",{detail:{element:e,file:t}});document.dispatchEvent(r)})).catch((n=>{e.innerHTML=`<p>Error loading ${t}</p>`}))}))}));